/**
 * PopoverGlass Component
 *
 * Floating glass-themed container for tooltips, dropdowns, and contextual overlays.
 * Provides compound and legacy APIs for maximum flexibility.
 *
 * ## Features
 * - Theme-aware glassmorphism styling (glass/light/aurora)
 * - Compound component API (PopoverGlass.Root, Trigger, Content, Anchor, Close)
 * - Legacy API for backward compatibility
 * - Arrow pointer with glass styling (optional)
 * - 12 positioning options (4 sides Ã— 3 alignments)
 * - Smooth animations with fade-in effect
 * - ESC key and click-outside to close
 * - Focus trap for accessibility
 * - Customizable width and padding via className
 * - Portal rendering for z-index isolation
 *
 * ## Sub-Components
 * - **PopoverGlass.Root** (or `PopoverGlass`) - Context provider with open/close state
 * - **PopoverGlassTrigger** - Trigger element (supports asChild pattern)
 * - **PopoverGlassAnchor** - Optional anchor for positioning
 * - **PopoverGlassContent** - Content container with glass styling
 * - **PopoverGlassLegacy** - Legacy wrapper API (backward compatible)
 *
 * ## CSS Variables
 * Customize appearance via theme CSS variables:
 * - `--popover-bg` - Background color (glass theme: `rgba(255,255,255,0.08)`)
 * - `--popover-border` - Border color (glass theme: `rgba(255,255,255,0.15)`)
 * - `--popover-shadow` - Box shadow with glow effect
 * - `--popover-arrow-bg` - Arrow pointer background color
 * - `--blur-md` - Backdrop blur amount (default: `16px`)
 *
 * @example Compound API (recommended)
 * ```tsx
 * import { PopoverGlass, PopoverGlassTrigger, PopoverGlassContent } from 'shadcn-glass-ui'
 *
 * <PopoverGlass>
 *   <PopoverGlassTrigger asChild>
 *     <ButtonGlass>Open Popover</ButtonGlass>
 *   </PopoverGlassTrigger>
 *   <PopoverGlassContent side="top">
 *     <div className="p-4">
 *       <h3 style={{ color: 'var(--text-primary)' }}>Title</h3>
 *       <p style={{ color: 'var(--text-secondary)' }}>Content</p>
 *     </div>
 *   </PopoverGlassContent>
 * </PopoverGlass>
 * ```
 *
 * @example Legacy API (backward compatible)
 * ```tsx
 * import { PopoverGlassLegacy } from 'shadcn-glass-ui'
 *
 * <PopoverGlassLegacy
 *   trigger={<ButtonGlass>Open</ButtonGlass>}
 *   side="top"
 * >
 *   <div className="p-4">Content</div>
 * </PopoverGlassLegacy>
 * ```
 *
 * @example Controlled state
 * ```tsx
 * const [open, setOpen] = useState(false)
 *
 * <PopoverGlass open={open} onOpenChange={setOpen}>
 *   <PopoverGlassTrigger>Open</PopoverGlassTrigger>
 *   <PopoverGlassContent>
 *     <ButtonGlass onClick={() => setOpen(false)}>Close</ButtonGlass>
 *   </PopoverGlassContent>
 * </PopoverGlass>
 * ```
 *
 * @example Without arrow
 * ```tsx
 * <PopoverGlassContent showArrow={false}>
 *   Content without arrow pointer
 * </PopoverGlassContent>
 * ```
 *
 * @accessibility
 * - **Keyboard Navigation:** ESC key closes popover (WCAG 2.1.1)
 * - **Focus Management:** Focus trapped within popover when open (WCAG 2.4.3)
 * - **Click Outside:** Clicking outside closes popover (WCAG 2.1.1)
 * - **Screen Readers:** Uses `role="dialog"` for content (WCAG 4.1.3)
 * - **ARIA Attributes:** Proper labeling for trigger and content relationship
 * - **Portal Rendering:** Content rendered outside DOM hierarchy to avoid z-index issues
 * - **Animations:** Smooth transitions that don't interfere with screen readers
 *
 * @since v1.0.0
 */

'use client';

import * as React from 'react';
import * as PopoverPrimitive from '@radix-ui/react-popover';
import { cn } from '@/lib/utils';
import '@/glass-theme.css';

// ========================================
// COMPOUND COMPONENT: ROOT
// ========================================

// Note: PopoverPrimitive.Root is a context provider and doesn't render DOM,
// so data-slot is applied to Content instead. This matches shadcn/ui pattern.
const PopoverGlassRoot = (props: React.ComponentPropsWithoutRef<typeof PopoverPrimitive.Root>) => (
  <PopoverPrimitive.Root {...props} />
);
PopoverGlassRoot.displayName = 'PopoverGlass';

// ========================================
// COMPOUND COMPONENT: TRIGGER
// ========================================

const PopoverGlassTrigger = React.forwardRef<
  React.ComponentRef<typeof PopoverPrimitive.Trigger>,
  React.ComponentPropsWithoutRef<typeof PopoverPrimitive.Trigger>
>((props, ref) => <PopoverPrimitive.Trigger data-slot="popover-trigger" ref={ref} {...props} />);
PopoverGlassTrigger.displayName = 'PopoverGlassTrigger';

// ========================================
// COMPOUND COMPONENT: ANCHOR
// ========================================

const PopoverGlassAnchor = React.forwardRef<
  React.ComponentRef<typeof PopoverPrimitive.Anchor>,
  React.ComponentPropsWithoutRef<typeof PopoverPrimitive.Anchor>
>((props, ref) => <PopoverPrimitive.Anchor data-slot="popover-anchor" ref={ref} {...props} />);
PopoverGlassAnchor.displayName = 'PopoverGlassAnchor';

// ========================================
// COMPOUND COMPONENT: CONTENT
// ========================================

/**
 * Props for PopoverGlassContent component.
 *
 * @example
 * ```tsx
 * const props: PopoverGlassContentProps = {
 *   side: 'top',
 *   align: 'center',
 *   sideOffset: 8,
 *   showArrow: true,
 *   className: 'w-96 p-6',
 * };
 * ```
 */
interface PopoverGlassContentProps extends React.ComponentPropsWithoutRef<
  typeof PopoverPrimitive.Content
> {
  /**
   * Whether to show the arrow pointer.
   *
   * @default true
   */
  showArrow?: boolean;
}

/**
 * PopoverGlassContent - Content container for PopoverGlass
 *
 * Defaults (matches shadcn/ui):
 * - `p-4` (16px padding)
 * - `w-72` (288px width)
 * - Both can be overridden via className prop
 *
 * @example
 * <PopoverGlassContent className="w-96 p-6"> // Custom size
 *   <p>Content</p>
 * </PopoverGlassContent>
 *
 * @example
 * <PopoverGlassContent className="p-0"> // Remove padding
 *   <div className="custom-padding">Content</div>
 * </PopoverGlassContent>
 */
const PopoverGlassContent = React.forwardRef<
  React.ElementRef<typeof PopoverPrimitive.Content>,
  PopoverGlassContentProps
>(({ className, align = 'center', sideOffset = 8, showArrow = true, children, ...props }, ref) => {
  // Popover content styles with CSS variables
  const popoverStyles: React.CSSProperties = {
    background: 'var(--popover-bg)',
    border: '1px solid var(--popover-border)',
    boxShadow: 'var(--popover-shadow)',
    backdropFilter: 'blur(var(--blur-md))',
    WebkitBackdropFilter: 'blur(var(--blur-md))',
  };

  // Arrow styles
  const arrowStyles: React.CSSProperties = {
    fill: 'var(--popover-arrow-bg)',
  };

  return (
    <PopoverPrimitive.Portal>
      <PopoverPrimitive.Content
        ref={ref}
        data-slot="popover-content"
        align={align}
        sideOffset={sideOffset}
        className={cn(
          'z-50003 w-72 rounded-2xl p-4',
          'animate-in fade-in-0 zoom-in-95 duration-200',
          'data-[side=bottom]:slide-in-from-top-2',
          'data-[side=top]:slide-in-from-bottom-2',
          'data-[side=right]:slide-in-from-left-2',
          'data-[side=left]:slide-in-from-right-2',
          'outline-none',
          className
        )}
        style={popoverStyles}
        {...props}
      >
        {children}

        {showArrow && (
          <PopoverPrimitive.Arrow
            className="fill-current"
            style={arrowStyles}
            width={16}
            height={8}
          />
        )}
      </PopoverPrimitive.Content>
    </PopoverPrimitive.Portal>
  );
});

PopoverGlassContent.displayName = 'PopoverGlassContent';

// ========================================
// LEGACY API (backward compatible)
// ========================================

/**
 * Props for PopoverGlassLegacy component (backward compatible API).
 *
 * @example
 * ```tsx
 * const props: PopoverGlassLegacyProps = {
 *   trigger: <ButtonGlass>Open</ButtonGlass>,
 *   children: <div>Content</div>,
 *   side: 'top',
 *   align: 'center',
 *   sideOffset: 8,
 *   showArrow: true,
 * };
 * ```
 */
export interface PopoverGlassLegacyProps {
  /** The trigger element that opens the popover */
  readonly trigger: React.ReactNode;
  /** The content to display inside the popover */
  readonly children: React.ReactNode;
  /**
   * The preferred side of the trigger to render against.
   *
   * @default "bottom"
   */
  readonly side?: 'top' | 'right' | 'bottom' | 'left';
  /**
   * The preferred alignment against the trigger.
   *
   * @default "center"
   */
  readonly align?: 'start' | 'center' | 'end';
  /**
   * The distance in pixels from the trigger.
   *
   * @default 8
   */
  readonly sideOffset?: number;
  /** Controlled open state */
  readonly open?: boolean;
  /** Callback when open state changes */
  readonly onOpenChange?: (open: boolean) => void;
  /**
   * Whether to show the arrow pointer.
   *
   * @default true
   */
  readonly showArrow?: boolean;
  /** Additional class name for the content wrapper */
  readonly className?: string;
}

const PopoverGlassLegacy = React.forwardRef<HTMLDivElement, PopoverGlassLegacyProps>(
  (
    {
      trigger,
      children,
      side = 'bottom',
      align = 'center',
      sideOffset = 8,
      open,
      onOpenChange,
      showArrow = true,
      className,
    },
    ref
  ) => {
    return (
      <PopoverGlassRoot open={open} onOpenChange={onOpenChange}>
        <PopoverGlassTrigger asChild>{trigger}</PopoverGlassTrigger>
        <PopoverGlassContent
          ref={ref}
          side={side}
          align={align}
          sideOffset={sideOffset}
          showArrow={showArrow}
          className={className}
        >
          {children}
        </PopoverGlassContent>
      </PopoverGlassRoot>
    );
  }
);

PopoverGlassLegacy.displayName = 'PopoverGlassLegacy';

// ========================================
// EXPORTS
// ========================================

// Compound API (shadcn/ui pattern)
export const PopoverGlass = PopoverGlassRoot;
export { PopoverGlassTrigger, PopoverGlassContent, PopoverGlassAnchor };

// Legacy API (backward compatible)
export { PopoverGlassLegacy };

// For backward compatibility, also export as default
export { PopoverGlassLegacy as default };
